import * as React from 'react';
import MapView, { Heatmap } from 'react-native-maps';
import { StyleSheet, Text, View, Dimensions } from 'react-native';
//import { points } from '../points';
import { useState } from 'react/cjs/react.development';
import axios from 'axios';
import AsyncStorage from '@react-native-async-storage/async-storage';



export function heatMap() {
  const [sstate, setState] = useState("WA")
  var lat = 0
  var lng = 0
  var DNI = 0
  getcoords()
  console.log(getData())
  var pointss = [];
  
  const storeData = async (value) => {
    try {
      
      await AsyncStorage.setItem('points', value)
    } catch (e) {
      
    }
  }
  
 
const getData = async () => {
  try {
    const array = await AsyncStorage.getItem('points')
    return array;
  } catch(e) {
    
  }
}
  for (let i = 0; i < 1; i++) {
      getDNI();
      
      var Obj = {};
      Obj['latitude'] = lat;
      Obj['longitude'] = lng;
      Obj['weight'] = DNI;
      pointss.push(Obj);
      lat+=1
      lng+=-1
  }
  console.log(pointss)
  const state = {
    initialPosition: {
      latitude: 37.0902,
      longitude: -95.7129,
      latitudeDelta: 57,
      longitudeDelta: 57
    }
  }
  
  
  
    return (
      <View style={styles.container}>

        <MapView
          provider="google"
          
          style={styles.map}
          initialRegion={state.initialPosition}>
          <Heatmap
            points={pointss}
            radius={40}
            opacity={1}
            gradient={{
              colors: ["black", "purple", "red", "orange", "white"],
              startPoints: Platform.OS === 'ios' ? [0.01, 0.04, 0.1, 0.45, 0.5] :
                [0.1, 0.25, 0.5, 0.75, 1],
              colorMapSize: 2000
            }}
          >
          </Heatmap>
        </MapView>
      </View>
    );
    function getDNI() {
        
      var options = {
        method: 'GET',
        url: 'https://developer.nrel.gov/api/solar/solar_resource/v1.json?api_key=0mnx2OSLok4UiEoaajb0fh4j2YoFOznGfGHZS78j&lat=' +lat + '&lon=' + lng,
        
      };
      console.log(options.url)
      axios.request(options).then(function (response) {
          
        DNI = response.data.outputs.avg_dni.annual
        
  
  
      }).catch(function (error) {
        console.error(error);
      });
      console.log(lat)
  }
    function getcoords() {
        
  
  
      var options = {
        method: 'GET',
        url: 'http://open.mapquestapi.com/geocoding/v1/address?key=saD6wA8skX1MAzw7nKIyqh9LhoxZ0a46&location=' + sstate,
        
      };
    
      axios.request(options).then(function (response) {
          console.log(response.data.results[0].locations[0].latLng.lat)
          lat = response.data.results[0].locations[0].latLng.lat
          lng = response.data.results[0].locations[0].latLng.lng
    
    
      }).catch(function (error) {
        console.error(error);
      });
    }
  
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  map: {
    width: Dimensions.get('window').width,
    height: Dimensions.get('window').height,
  },
});
