import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, TextInput } from 'react-native';
import { Button } from 'react-native';
import { useState } from 'react/cjs/react.development';
import { Axios } from 'axios';

const [address, setAddress] = useState("null")
  const [lat, setLat] = useState("null")
  const [long, setLong] = useState("null")
  const [energy, setEnergy] = useState("1")
  const [PR, setPR] = useState("0.75")
  const [H, setH] = useState("0")
  const [Area, setArea] = useState("25")
  const [r, setr] = useState("18")

export default function App() {




  function getDNI() {
    getcoords()
    var axios = require("axios").default;
    var options = {
      method: 'GET',
      url: 'https://developer.nrel.gov/api/solar/solar_resource/v1.json?api_key=0mnx2OSLok4UiEoaajb0fh4j2YoFOznGfGHZS78j&lat=' +lat + '&lon=' + long,

    };

    axios.request(options).then(function (response) {

      setH(response.data.outputs.avg_dni.annual)



    }).catch(function (error) {
      console.error(error);
    });
  }

  function getcoords() {
    var axios = require("axios").default;
    var options = {
      method: 'GET',
      url: 'http://open.mapquestapi.com/geocoding/v1/address?key=saD6wA8skX1MAzw7nKIyqh9LhoxZ0a46&location=1600+Pennsylvania+Ave+NW,Washington,DC,20500',

    };

    axios.request(options).then(function (response) {
        setLat(response.data.results[0].locations[0].latLng.lat);
        setLong(response.data.results[0].locations[0].latLng.lng);



    }).catch(function (error) {
      console.error(error);
    });
  }

  function calculateEnergy() {

    getDNI()
    console.log('h' + H)
    setEnergy(Area*r*H*PR)
    console.log(energy + "KWH")

  }

  return (
    <View style={styles.container}>

      <Text> Enter Address </Text>
      <TextInput style={styles.input} 

        onChangeText={(val) => setAddress(val)}
      />
      <Button title='Submit' onPress={() => calculateEnergy() } />
      <Text> {energy} </Text>

    </View>
  );
}




const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  input: {
    borderWidth: 1,
    borderColor: '#777',
    padding: 8,
    margin: 10,
    width: 200,

  }
});
