      await AsyncStorage.setItem('points', value)
    } catch (e) {
      
    }
  }
  
 
const getData = async () => {
  try {
    const array = await AsyncStorage.getItem('@storage_Key')
    return array;
  } catch(e) {
    
  }
}
  for (let i = 0; i < 1; i++) {
      getDNI();
      
      var Obj = {};
      Obj['latitude'] = lat;
      Obj['longitude'] = lng;
      Obj['weight'] = DNI;
      pointss.push(Obj);
      lat+=1
      lng+=-1
  }
  console.log(pointss)
  const state = {
    initialPosition: {
      latitude: 37.0902,
      longitude: -95.7129,
      latitudeDelta: 57,
      longitudeDelta: 57
    }
  }
  */
  
  
    return (

      <View style={styles.container}>
  
        <MapView
          provider="google"
          
          style={styles.map}
          initialRegion={state.initialPosition}>
          <Heatmap
            points={pointss}
            radius={40}
            opacity={1}
            gradient={{
              colors: ["black", "purple", "red", "orange", "white"],
              startPoints: Platform.OS === 'ios' ? [0.01, 0.04, 0.1, 0.45, 0.5] :
                [0.1, 0.25, 0.5, 0.75, 1],
              colorMapSize: 2000
            }}
          >
          </Heatmap>
        </MapView>
      </View>
    );

    async function getDNI() {
      try {
         let res = await axios({
              url: 'https://developer.nrel.gov/api/solar/solar_resource/v1.json?api_key=0mnx2OSLok4UiEoaajb0fh4j2YoFOznGfGHZS78j&lat=' +lat + '&lon=' + lng,
              method: 'get',
              timeout: 1000,
              
          })
           
          
          return res.data
      }
      catch (err) {
          console.error(err);
      }
  }
  
  async function getcoords() {
    try {
       let res = await axios({
            url: 'http://open.mapquestapi.com/geocoding/v1/address?key=saD6wA8skX1MAzw7nKIyqh9LhoxZ0a46&location=' + sstate,
            method: 'get',
            timeout: 1000,
            
        })
         
        
        return res.data
    }
    catch (err) {
        console.error(err);
    }
}
    
  
  
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  map: {
    width: Dimensions.get('window').width,
    height: Dimensions.get('window').height,
  },
});
