import * as React from 'react';
import MapView, { Heatmap } from 'react-native-maps';
import { StyleSheet, Text, View, Dimensions } from 'react-native';
import { points } from '../points';
import { useState } from 'react/cjs/react.development';
import axios from 'axios';



export function heatMap() {
  const [sstate, setState] = useState("WA")
  var lat = 0
  var lng = 0
  
  getcoords()
  
  const state = {
    initialPosition: {
      latitude: 37.0902,
      longitude: -95.7129,
      latitudeDelta: 57,
      longitudeDelta: 57
    }
  }

  console.log(lat)
  console.log(lng)
  
    return (
      <View style={styles.container}>

        <MapView
          provider="google"
          
          style={styles.map}
          initialRegion={state.initialPosition}>
          <Heatmap
            points={points}
            radius={40}
            opacity={1}
            gradient={{
              colors: ["black", "purple", "red", "orange", "white"],
              startPoints: Platform.OS === 'ios' ? [0.01, 0.04, 0.1, 0.45, 0.5] :
                [0.1, 0.25, 0.5, 0.75, 1],
              colorMapSize: 2000
            }}
          >
          </Heatmap>
        </MapView>
      </View>
    );
    function getcoords() {
        
  
  
      var options = {
        method: 'GET',
        url: 'http://open.mapquestapi.com/geocoding/v1/address?key=saD6wA8skX1MAzw7nKIyqh9LhoxZ0a46&location=' + sstate,
        
      };
    
      axios.request(options).then(function (response) {
          console.log(response.data.results[0].locations[0].latLng.lat)
          responses = response
          lat = response.data.results[0].locations[0].latLng.lat
          lng = response.data.results[0].locations[0].latLng.lng
    
    
      }).catch(function (error) {
        console.error(error);
      });
    }
  
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  map: {
    width: Dimensions.get('window').width,
    height: Dimensions.get('window').height,
  },
});
